import React from 'react'
import { Card } from "./card";
export const CardShelf = ({bookshelf,removeFromBookshelf}) => {
    if(bookshelf.length===0)
        {
            return (
                <div className="grid xs:grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-5 p-2 mx-auto max-w-6xl text-3xl items-center">
                  Empty BookShelf
                </div>
            )
        }
  return (
    <div className="grid xs:grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-5 p-2 mx-auto max-w-6xl">
      {bookshelf.map((book, index) => (
        <Card key={index} book={book} removeFromBookshelf={removeFromBookshelf} bookshelf={bookshelf} isInBookshelf={bookshelf.some((b) => b.key === book.key)}/>
      ))}
    </div>
  )
}
