import React, { useEffect, useState } from "react";
import axios from "axios";
import { CardList } from "./CardList";

export const SearchBar = () => {
  const [bookname, setBookName] = useState("");
  const [books, setBooks] = useState([]);

  const getBookData = async (bookname) => {
    const res = await axios.get(
      `http://openlibrary.org/search.json?q=${bookname}&limit=10&page=1`
    );
    setBooks(res.data.docs);
    console.log("Data", res.data.docs);
  };

  useEffect(() => {
    getBookData(bookname);
  }, [bookname]);

  return (
    <div className="w-11/12 max-w-3xl mx-auto text-black flex flex-col items-center gap-5">
      <div className="w-full">
        <label htmlFor="bookSearch" className="block text-lg font-medium text-gray-700 mb-2">
          Search Book Name
        </label>
        <input
          type="text"
          id="bookSearch"
          placeholder="Type the book name here..."
          value={bookname}
          onChange={(event) => setBookName(event.target.value)}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>
      <CardList books={books} />
    </div>
  );
};
