import React from 'react'
import { Card } from "./card";
export const CardShelf = ({bookshelf,removeFromBookshelf}) => {
  return (
    <div className="grid xs:grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-5 p-2 mx-auto max-w-6xl">
      {bookshelf.map((book, index) => (
        <Card key={index} book={book} removeFromBookshelf={removeFromBookshelf} bookshelf={bookshelf}/>
      ))}
    </div>
  )
}
