import React from "react";

export const Card = ({ book }) => {
  return (
    <div
      className={`w-[360px] lg:w-[30%] 
           "bg-white shadow-[12px_12px_0_0] shadow-yellow-50 text-richblack-100"`}>
      <div className="font-bold text-xl mt-2 p-2">Title:{book.title}</div>
      <div className="mt-2 p-2">Rating:{book.ratings_average}</div>
      <div className="flex flex-row justify-between p-5">
        <div>Type:{book.type}</div>
        <div>Edition Count:{book.edition_count}</div>
      </div>
    </div>
  );
};
